package com.dca.assignment;

import java.util.*;

public class SubsequenceClass {
	public static boolean isSubsequence(String s, String t) {
        List<Integer> index=new ArrayList<>();
        if(s.equals("")) return true;
        for(int i=0;i<s.length();i++){
            if(t.contains(s.charAt(i)+"")){
                index.add(t.indexOf(s.charAt(i)+""));
            }
        }
        if(index.size()==0) return false;
        int count=0;
        for(int i=index.size()-1;i>=1;i--){
            if(index.get(i-1)<=index.get(i))
                count++;
        }
        int size=index.size()-1;
        if(count==index.size()-1) return true;
        return false;
    }
	/*
	 * is
	 */
	public static void main(String[] args) {
		String a="leeeeetcode";
		String b="";
		System.out.println(isSubsequence(a,b));
	}
}
